<?php$mediaeventid = $_GET["mediaeventid"];if ($mediaeventid == null || $mediaeventid == "") {    echo("<h1>No Mediaevent is selected!!</h1><br>via GET Parameter mediaeventid");} else {    showFotoList($mediaeventid);}function showFotoList($mediaeventid) {    $mediaevent = getMediaeventById($mediaeventid);    if ($mediaevent == null) {        echo("<h1>MediaEvent Not Found!</h1>");        return;    }    if ($mediaevent->isPrivate() && !checkPrivateFotos()) {        echo("<h1>Privates Event darf nur von eingeloggten<br>mit den entsprechenden Rechten gesehen werden.!</h1>");        return;    }    $mediaKind = getMediaKindById($mediaevent->getMediakindId());    $mediacathegory = getMediacathegoryById($mediaevent->getMediacathegoryId());    echo("<h1>" . utf8_encode($mediaevent->getEventName()) . "</h1>");    $path = $mediaKind->getDirectory() . "/" . $mediacathegory->getDirectory() . "/" . $mediaevent->getYear() . "/" . $mediaevent->getDirectoryEvent() . "/" . $mediaevent->getZipfile();    echo("<a href='" . htmlentities($path) . "' class='links'>Alle Fotos gezippt (" . $mediaevent->getZipsize() . " Bytes)</a>");    echo("<br>");    echo("<br>");    $count = 0;    foreach ($mediaevent->getMediaItems() as $mediaItem) {        $fotopath = $mediaKind->getDirectory() . "/" . $mediacathegory->getDirectory() . "/" . $mediaevent->getYear() . "/" . $mediaevent->getDirectoryEvent() . "/" . $mediaevent->getDirectorymain() . "/" . $mediaItem->getFilename();        $thumnailpath = $mediaKind->getDirectory() . "/" . $mediacathegory->getDirectory() . "/" . $mediaevent->getYear() . "/" . $mediaevent->getDirectoryEvent() . "/" . $mediaevent->getDirectoryThumbs() . "/" . $mediaItem->getFilename();        $comment = $mediaItem->getComment();        if ($comment == null || $comment == '') {            $comment = $mediaevent->getEventName();        }        echo("<a href='");        echo(htmlentities($fotopath));        echo("'  class='fancyBoxClass'");        echo("rel='Fotos'");        echo(" title='");        echo(htmlentities($mediaevent->getEventName()));        echo("'> <img src='");        echo(htmlentities($thumnailpath));        echo("' border='0' width='");        echo($mediaItem->getThumbwidth());        echo("' height='");        echo($mediaItem->getThumbheight());        echo("' alt='");        echo(htmlentities($mediaItem->getFilename()));        echo(" size(");        echo($mediaItem->getMainwidth());        echo(",");        echo($mediaItem->getMainheight());        echo(") ");        echo($mediaItem->getBytes());        echo(" Bytes ");        echo("'> </a>\n");        $count++;    }}?><br><br>